import { Pinecone } from '@pinecone-database/pinecone';
import OpenAI from 'openai';
import type { RIASECScores } from '@shared/schema';

const pinecone = new Pinecone({
  apiKey: process.env.PINECONE_API_KEY || ''
});

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || ''
});

const INDEX_NAME = 'riasec-cases';
const DIMENSION = 1536; // OpenAI text-embedding-ada-002 dimension

export interface CaseStudy {
  id: string;
  riasecScores: RIASECScores;
  selectedMajor: string;
  satisfactionRating: number;
  description: string;
  graduationYear?: number;
  careerPath?: string;
}

export class PineconeService {
  private index: any;

  async initialize() {
    try {
      // Check if index exists, create if not
      const existingIndexes = await pinecone.listIndexes();
      const indexExists = existingIndexes.indexes?.some(index => index.name === INDEX_NAME);

      if (!indexExists) {
        console.log(`Creating Pinecone index: ${INDEX_NAME}`);
        await pinecone.createIndex({
          name: INDEX_NAME,
          dimension: DIMENSION,
          metric: 'cosine',
          spec: {
            serverless: {
              cloud: 'aws',
              region: 'us-east-1'
            }
          }
        });

        // Wait for index to be ready
        await new Promise(resolve => setTimeout(resolve, 5000));
      }

      this.index = pinecone.index(INDEX_NAME);
      console.log('Pinecone service initialized successfully');

      // Initialize with sample data if empty
      await this.seedSampleData();

    } catch (error) {
      console.error('Failed to initialize Pinecone:', error);
      throw error;
    }
  }

  async generateEmbedding(text: string): Promise<number[]> {
    try {
      const response = await openai.embeddings.create({
        model: 'text-embedding-ada-002',
        input: text,
      });

      return response.data[0].embedding;
    } catch (error) {
      console.error('Failed to generate embedding:', error);
      throw error;
    }
